import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#967bb6', // This is just an example of dark lavender
    },
    text: {
      primary: '#343d46', //'#967bb6', // Dark lavender for text
    }
  },
  typography: {
    fontFamily: 'Figtree, Arial, sans-serif',
    h2: {
        background: '-webkit-linear-gradient(45deg, #6a4595, #9a77cf)', // Deeper gradient from dark lavender to a lighter shade
        WebkitBackgroundClip: 'text',
        fontSize: '4rem', // Sets a larger font size for all buttons
        WebkitTextFillColor: 'transparent',
        //textShadow: '0px 3px 6px rgba(106, 69, 149, 0.6)', // Darker and more pronounced shadow
        fontWeight: 'bold',
      },
    button: {
        textTransform: 'none',
        fontSize: '1.2rem', // Sets a larger font size for all buttons
        color: 'black',//'#967bb6', // Ensures button text color is dark lavender
        padding: '0rem'
      },
    body1: {
        fontSize: '1.3rem', // Sets a larger font size for all buttons

    }
  },
});


export default theme;
