import React, { useRef, useEffect } from 'react';
import Navbar from './Navbar';
import LandingPage from './LandingPage';
import { ThemeProvider, Box, Typography } from '@mui/material/styles';
import theme from './theme';
import banner from './banner2.mp4';  // Ensure this is the correct path

function App() {
  const videoRef = useRef(null); // Create a ref for the video element

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.playbackRate = 0.75; // Adjust this value to control speed
    }
  }, []);

  return (
    <div>
      <ThemeProvider theme={theme}>
        <Box sx={{
          position: 'relative', 
          width: '100vw', 
          height: '100vh', 
          overflow: 'hidden', 
          paddingBottom: '2rem'
        }}>
          {/* Fullscreen Video Banner */}
          <video ref={videoRef} autoPlay muted loop style={{ width: '100%', height: '100%', objectFit: 'cover' }}>
            <source src={banner} type="video/mp4" />
            Your browser does not support the video tag.
          </video>

          {/* Text Overlay */}
          <Box sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 2  // Ensures the text is above all other content
          }}>
            <Typography variant="h4" component="h2" sx={{ color: 'white', textShadow: '2px 2px 8px rgba(0,0,0,0.7)' }}>
              Inspiring Innovation
            </Typography>
          </Box>

          {/* Overlay Navbar */}
          <Navbar />
        </Box>
        <LandingPage />
      </ThemeProvider>
    </div>
  );
}

export default App;
