import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#E6E6FA', // Lavender color
    },
    text: {
      primary: '#E6E6FA', // Lavender color for text
    }
  },
  typography: {
    fontFamily: 'Figtree, Arial, sans-serif',
    button: {
      textTransform: 'none', // Optional: prevents uppercase transformation for buttons
    },
  },
});

export default theme;
