import React from 'react';
import { useRef, useEffect } from 'react';
import Navbar from './Navbar';
import LandingPage from './LandingPage';
import { ThemeProvider } from '@mui/material/styles';
import theme from './theme';
import { Box, Typography, Container } from '@mui/material';
import banner from './banner2.mp4'


function App() {
  const videoRef = useRef(null); // Create a ref for the video element

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.playbackRate = 0.5; // Adjust this value to control speed, 0.5 is half speed
    }
  }, []);

  return (
    <div>
      {/* Wrap all components that should be themed within ThemeProvider */}
      <ThemeProvider theme={theme}>
        <Box sx={{ position: 'relative', width: '100vw', height: '100vh', overflow: 'hidden', paddingBottom: '2rem' }}>
          {/* Fullscreen Video Banner */}
          <video ref={videoRef} autoPlay muted loop style={{ width: '100%', height: '100%', objectFit: 'cover' }}>
            <source src={banner} type="video/mp4" />
            Your browser does not support the video tag.
          </video>

          {/* Overlay Navbar */}
          <Navbar />
        </Box>
        <LandingPage />
      </ThemeProvider>
    </div>
  );
}

export default App;
